mapper.select:
SELECT 
	buyerCode,
	buyerName,
	address,
	country,
	businessNumber,
	phone,
	deliveryCompany,
	manager
FROM Buyer



		OR buyerName Like #{buyerInfo}


		OR buyerCode LIKE #{buyerInfo}


		OR address Like #{buyerInfo}


		OR country LIKE #{buyerInfo}


		OR manager LIKE #{buyerInfo}

SELECT productCode, productType, productName, weight, size, price, unit, content, inserted FROM Product
OR productName Like #{productInfo}
OR productType Like #{productInfo}
OR productCode Like #{productInfo}
