mapper.select:
SELECT h.orderId, h.inserted, h.modified, h.deliveryDate, h.writer, h.buyerCode, h.status, h.message, h.comment,
i.productCode, i.finalPrice, i.quantity, i.sum,
p.productName, p.productType, p.size FROM Order_item i LEFT JOIN Order_header h ON i.orderId = h.orderId LEFT JOIN Product p ON i.productCode = p.productCode
OR i.productCode LIKE #{orderQ}
OR p.productName LIKE #{orderQ}
OR p.productType LIKE #{orderQ}
OR p.size LIKE #{orderQ}
AND i.productCode = #{productCode}
AND p.productName = #{productName}
AND p.productType = #{productType}
AND p.size = #{size}
ORDER BY h.orderId DESC
